    const [toggleMenu, setMenuOpen] = useState(false);

   const handleOnClick = (e) => {
       console.log("i clicked");
       if (menuOpen === true) {
           setMenuOpen(false);
       }
   };

    return(
        <div>
        <Menu isOpen={menuOpen} right width={ '100vw' } >
            <HashLink to="#home" onClick={handleOnClick}>Home</HashLink>
            <HashLink to="#about" onClick={handleOnClick}>About me</HashLink>
            <HashLink to="#contact" onClick={handleOnClick}>Contact</HashLink>           
        </Menu>
        </div>
    )
}






