import React, { useState, useContext } from 'react';
import '../styles/navbar.scss';
import { slide as Menu } from "react-burger-menu";
import { HashLink } from 'react-router-hash-link';

const MyContext = React.createContext();

const MyProvider = (props) => {
    const [menuOpenState, setMenuOpenState] = useState(false);

    return(

        <MyContext.Provider value={{
            isMenuOpen: menuOpenState,
            toggleMenu: () => setMenuOpenState(!menuOpenState),
            stateChangeHandler: (newState) => setMenuOpenState(newState.isOpen)
        }}>
            {props.children}
        </MyContext.Provider>
    )
}
const Navbar = (props) => {

    const ctx = useContext(MyContext);

    return (

        <div>
        <MyProvider>

        </MyProvider>
        <Menu isOpen={ctx.isMenuOpen} onStateChange={(state) => ctx.stateChangeHandler(state)} right width={ '100vw' } >
            <HashLink to="#home" onClick={ctx.toggleMenu}>Home</HashLink>
            <HashLink to="#about" onClick={ctx.toggleMenu}>About me</HashLink>
            <HashLink to="#contact" onClick={ctx.toggleMenu}>Contact</HashLink>           
        </Menu>
        </div>
    )
}






