import React, { Component } from 'react';
import '../styles/navbar.scss';
import { slide as Menu } from "react-burger-menu";
import { Link } from "react-router-dom";



export class Navbar extends Component {

    // constructor (props) {
    //     super(props)
    //     this.state = {
    //         menuOpen: false
    //     }
    // }

    handleStateChange (state) {
        this.setState({menuOpen: state.isOpen})
    }

    closeMenu () {
        this.setState({menuOpen: false})
    }

    toggleMenu () {
        this.setState(state => ({menuOpen: !state.menuOpen}))
    } 

    render () {
        return(
            <div>
            <Menu isOpen={this.state.menuOpen} onStateChange={(state) => this.handleStateChange(state)} right width={ '100vw' } >
                <Link to="/" onClick={() => this.closeMenu()}>Home</Link>
                <Link to="/about" onClick={() => this.closeMenu()}>About</Link>
                <Link to="/work" onClick={() => this.closeMenu()}>Work</Link>
                <Link to="/contact" onClick={() => this.closeMenu()}>Contact</Link>           
            </Menu>
            </div>
        )
    }
}

export default Navbar;
